class Realm(object):
    """
    A passive object for holding realm attributes.
    """
    def __init__(self, addr, mask, eth, realm, type, sigpool, medpool):
        self.configValues = {
            'rsa': addr,
            'mask': mask,
            'ifname': eth,
            'realm' : realm,
            'type': type,
            'sigpool': sigpool,
            'medpool': medpool,
        }

    def setValue(self, key, value):
        self.configValues[key] = value

    def getValue(self, key):
        if key in self.configValues.keys():
            return self.configValues[key]
        return '\'\''

