class NxgenInfo(object):
    """
    A passive object for holding Nxgen attributes.
    """
    def __init__(self, mediaip, mgenmgmtip):
        self.configValues = {
            'mediaip': mediaip,
            'mgenmgmtip': mgenmgmtip
        }

    def setValue(self, key, value):
        self.configValues[key] = value

    def getValue(self, key):
        if key in self.configValues.keys():
            return self.configValues[key]
        return '\'\''

